
public class ChracterDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print('A');
		char ch = 'A' +1;//65+1 = 66(B)
		System.out.print(ch);
		System.out.print('\n');
		ch = 'A'+2;
		System.out.print(ch);
		System.out.print('D');
		System.out.print('\b');//한칸 back
		System.out.print('E');
		System.out.print('\t');
		System.out.print('F');
		System.out.print('한');
		System.out.print('지');
		System.out.print('민'); //chracter 1개의 글자 , 특수문자 , 출력포맷 = %c
	}

}
